TemplateSQL.FixedSQL:
SET NOCOUNT ON /*********************************************/ /* 메모테이블 - T_MEMO 조회 - SELECT QUERY */ /*********************************************/ SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED DECLARE @PAGE_COUNT INT = {0}, @PAGE INT = {1};
WITH T_TABLE AS ( SELECT ROW_NUMBER() OVER(ORDER BY {2}) ROW_IDX , A.IDX , A.TABLE_NAME , A.TABLE_KEY , A.MEMO , A.MEMO1 , A.MEMO2 , A.INSERT_CODE , A.INSERT_DATE FROM T_MEMO A WHERE 1=1 #[IDX] #[TABLE_NAME] #[TABLE_KEY] ) SELECT *, (SELECT COUNT(*) FROM T_TABLE) AS TOTAL_ROWCOUNT FROM T_TABLE WHERE ROW_IDX > (@PAGE - 1) * @PAGE_SIZE AND ROW_IDX <= @PAGE * @PAGE_SIZE
SET NOCOUNT OFF
