TemplateSQL.FixedSQL:
SET NOCOUNT ON /**********************************************************************/ /* 웹메뉴 그룹별 메뉴권한 테이블 - T_STORE_WEBMENU_EMPLOYEE_MENU 조회 */ /* - SELECT QUERY */ /**********************************************************************/ SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED DECLARE @PROJECT_SITE INT = {0} , @STORE_CODE INT = {1} , @MEMBER_CODE INT = {2}
SELECT A.PROJECT_SITE , A.STORE_CODE , @MEMBER_CODE MEMBER_CODE , A.MENU_CODE , A.SEARCH_CODE , A.NAME AS MENU_NAME , CONVERT(BIT, ISNULL(B.INSERT_AUTH, 1)) AS INSERT_AUTH , CONVERT(BIT, ISNULL(B.UPDATE_AUTH, 1)) AS UPDATE_AUTH , CONVERT(BIT, ISNULL(B.EXCEL_AUTH,1)) AS EXCEL_AUTH , CONVERT(BIT, ISNULL(B.PRINT_AUTH, 1)) AS PRINT_AUTH , CONVERT(BIT, ISNULL(B.HIDE, 1)) AS HIDE , B.REMARK
, B.INSERT_CODE , B.INSERT_DATE , B.UPDATE_CODE , B.UPDATE_DATE , C.USER_NAME AS UPDATE_NAME , A.MENU_URL FROM V_STORE_MENU A LEFT JOIN T_STORE_WEBMENU_EMPLOYEE_MENU B ON B.PROJECT_SITE = A.PROJECT_SITE AND B.STORE_CODE = A.STORE_CODE AND B.MEMBER_CODE = @MEMBER_CODE AND B.MENU_CODE = A.MENU_CODE --AND B.HIDE = 0 LEFT JOIN T_MEMBER C ON B.UPDATE_CODE = C.MEMBER_CODE WHERE A.PROJECT_SITE = @PROJECT_SITE AND A.STORE_CODE = @STORE_CODE AND A.HIDE = 0 --AND CASE WHEN @MEMBER_CODE = 0 THEN 0 ELSE 1 END = 1 ORDER BY A.SEARCH_CODE
