TemplateSQL.FixedSQL:
SET NOCOUNT ON /***************************************************/ /* 로컬박스별 광고 리스트 - GetAdDeviceList SELECT QUERY */ /***************************************************/ SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED DECLARE @PAGE INT = {0}, @PAGE_COUNT INT ={1} DECLARE @DEVICE_CODE INT = {2};
WITH T_TABLE AS ( SELECT ROW_NUMBER() OVER(ORDER BY A.AD_CODE DESC) AS SEQ ,A.AD_CODE, A.LOGO_URL, A.TITLE ,B.FR_DATE, B.TO_DATE ,B.DEVICE_CODE ,ISNULL(B.HIDE,1) AS HIDE FROM T_AD A LEFT JOIN T_AD_DEVICE B ON B.AD_CODE = A.AD_CODE AND B.DEVICE_CODE = @DEVICE_CODE LEFT JOIN T_STORE C ON '|' + A.STORE_CODE + '|' LIKE '%|' + CONVERT(VARCHAR(10), C.STORE_CODE) +'|%' WHERE A.HIDE = 0
) SELECT *, (SELECT COUNT(1) FROM T_TABLE) TOTAL_ROWCOUNT FROM T_TABLE WHERE SEQ > (@PAGE -1) * @PAGE_COUNT AND SEQ <= @PAGE * @PAGE_COUNT
SET NOCOUNT OFF
