SET NOCOUNT ON /*****************************************************************/ /* 로컬박스별 광고 플레이 로그 - T_AD_PLAY_LOG 저장 - AdPlayLogSave */ /*****************************************************************/ DECLARE @IDX BIGINT = N'{0}' , @REG_DATE DATETIME = CASE WHEN ISDATE('{1}') = 1 THEN '{1}' ELSE GETDATE() END , @DEVICE_KIND INT = CONVERT(INT, CASE WHEN ISNUMERIC('{2}') = 1 THEN '{2}' ELSE '0' END ) , @DEVICE_CODE BIGINT = CONVERT(BIGINT, CASE WHEN ISNUMERIC('{3}') = 1 THEN '{3}' ELSE NULL END ) , @AD_CODE BIGINT = CONVERT(BIGINT, CASE WHEN ISNUMERIC('{4}') = 1 THEN '{4}' ELSE '0' END ) , @FRAME_TYPE INT = CONVERT(INT, CASE WHEN ISNUMERIC('{5}') = 1 THEN '{5}' ELSE '0' END ) , @PLAY_TYPE INT = CONVERT(INT, CASE WHEN ISNUMERIC('{6}') = 1 THEN '{6}' ELSE '0' END ) , @PLAY_TIME NUMERIC(7,3) = CONVERT(NUMERIC(7,3), CASE WHEN ISNUMERIC('{7}') = 1 THEN '{7}' ELSE '0' END ) , @REMARK NVARCHAR(200) = N'{8}' , @INSERT_CODE INT = CASE WHEN ISNUMERIC('{9}') = 1 THEN '{9}' ELSE 0 END
/*UPDATE A SET REG_DATE = @REG_DATE , DEVICE_KIND = @DEVICE_KIND , DEVICE_CODE = @DEVICE_CODE , AD_CODE = @AD_CODE , PLAY_TIME = @PLAY_TIME , PLAY_TIME = @PLAY_TIME , REMARK = @REMARK FROM T_AD_PLAY_LOG A WHERE IDX = @IDX */
/*클릭 했을 경우 60초로 등록*/ SET @PLAY_TIME = CASE WHEN @PLAY_TIME = 0 AND @PLAY_TYPE = 2 THEN 60 ELSE @PLAY_TIME END UPDATE A SET PLAY_TIME = @PLAY_TIME FROM T_AD_PLAY_LOG A WHERE IDX = @IDX IF @@ROWCOUNT = 0 BEGIN INSERT INTO T_AD_PLAY_LOG ( REG_DATE , DEVICE_KIND , DEVICE_CODE , AD_CODE , FRAME_TYPE , PLAY_TYPE , PLAY_TIME , REMARK , INSERT_CODE ) SELECT @REG_DATE , @DEVICE_KIND , @DEVICE_CODE , @AD_CODE , @FRAME_TYPE , @PLAY_TYPE , @PLAY_TIME , @REMARK , @INSERT_CODE
SET @IDX = IDENT_CURRENT('T_AD_PLAY_LOG') END SELECT CONVERT(VARCHAR(100), @IDX) AS DATA SET NOCOUNT OFF
